import React from 'react'
import { NavLink } from 'react-router-dom'
import {Dropdown} from 'semantic-ui-react'


const DropdownWriter = () => {
    return (
    <>
    <NavLink to='/createpublication/'><Dropdown.Item as='li'>Create a Publication</Dropdown.Item></NavLink>
    <NavLink to='/mypublications/'><Dropdown.Item as='a'>My publication</Dropdown.Item></NavLink>
    </>)
}

const DropdownModerator = () => {
    return (
    <>
    <NavLink to='/pendingPublication/'><Dropdown.Item as='a'>Create a Publication</Dropdown.Item></NavLink>
    </>
    )

}

const DropdownCoordinator = () => {
    return (
    <>
    <NavLink to='/createColumn/'><Dropdown.Item as='a'>Create a Publication</Dropdown.Item></NavLink>
    <NavLink to='/myColumn/'><Dropdown.Item as='a'>My publication</Dropdown.Item></NavLink>
    </>
    )
}


export const ProfileMenu = {
    writer: <DropdownWriter />,
    coordinator: <DropdownCoordinator />,
    moderator: <DropdownModerator />,
}
