import React, {useEffect, useState} from 'react';
import CreatePubForm from '../components/CreatePubForm'
import {authenticationService, authAxios} from '../services'
import {api} from '../helpers/api'



const CreatePub = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  async function fetchData() {
    setLoading(true);
    try {
        const res = await authAxios.get(`${api.pub.base}createpub/`);
        console.log(res.data);
        setLoading(false)
    } catch (err) {
        setError(err.message)
        setLoading(false)
    } 
  }

  useEffect(() => {

      if(authenticationService.isAuthenticated){
        fetchData()
      }
    }, [])


  return(
    <CreatePubForm/> 

  )

}
export default CreatePub;
