import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Item, Dimmer, Loader, Segment, Divider, Icon, Grid} from 'semantic-ui-react'
import axios from 'axios'
import Message from '../components/Message'


const PubFeed = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            // const res = await axios.get('http://127.0.0.1:8000/feed/', {headers: {Authorization: 'Token 4f02ece2a0591ae26009f68bd0bce6b1ce60d09b' }});
            const res = await axios.get('http://127.0.0.1:8000/publication/');
            console.log(res.data.results);

            setPub(res.data.results);
            console.log(publication)
            setLoading(false)
        } catch (err) {
            setError(err.message)
            setLoading(false)
        } 
      }
      fetchData();
    }, [])

    function timeConversion(millisec) {
      var seconds = (millisec / 1000).toFixed(1);
      var minutes = (millisec / (1000 * 60)).toFixed(1);
      var hours = (millisec / (1000 * 60 * 60)).toFixed(1);
      var days = (millisec / (1000 * 60 * 60 * 24)).toFixed(1);

      if (seconds < 60) {
          return seconds + " Sec";
      } else if (minutes < 60) {
          return minutes + " Min";
      } else if (hours < 24) {
          return hours + " Hrs";
      } else {
          return days + " Days"
      }
  }

    function parseISOString(s) {
      var b = s.split(/\D+/);
      var date_time = new Date(Date.UTC(b[0], --b[1], b[2], b[3], b[4], b[5], b[6]))
      var today = new Date()
      return timeConversion(today.getTime() - date_time.getTime())
    }




  return(

    <div>
      <Grid
          centered

          padded
          stackable
          style={{ margin: '-1.5em' }}
          textAlign='center'
        >
          <Grid.Column color='yellow' textAlign='center' style={{ margin: '0.5em', height: 50 }}>
          <Header as='h1'> Feed </Header>
          </Grid.Column>
          </Grid>

      {publication ?
      publication.map(x => {
        return(
          <div>
          <Item key={x.id}>
          <Item.Image size="medium" src={x.thumbnail} />
            <Item.Content>
              <Item.Header as='h2'>{x.title}</Item.Header>
        <Item.Extra><Icon name='time'/> : {parseISOString(x.date_time_posted)} | {x.section}</Item.Extra>
            </Item.Content>
          </Item>
          <Divider/>
          </div>


        )
      })
      :
      <Segment>
      <Dimmer active inverted>
        <Loader inverted content='Loading' />
      </Dimmer>
      </Segment>
      }

      <Item.Group>

      {error && <Message negative message={error}/>}
      </Item.Group>




    </div>
  )
}
export default PubFeed;
