import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react';
import {Header, Image, Item, Icon} from 'semantic-ui-react'
import axios from 'axios'
import Message from '../components/Message'
import {useParams} from 'react-router-dom'
import {api} from '../helpers/api'
import MarkdownIt from 'markdown-it'



const mdParser = new MarkdownIt(/* Markdown-it options */);
const PubDetail = () => {

  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)
  const params = useParams()


  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
          const res = await axios.get(`${api.pub.list}${params['pubid']}/`);
          setPub(res.data);
          setLoading(false)
      } catch (err) {
          setError(err.message)
          setLoading(false)
      } 
      console.log()
    }
    fetchData();
  }, [])

  const get_date = () => {
    let d = new Date(publication.date_time_posted)
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    return (
      `${d.getDate()} ${months[d.getMonth()]} ${d.getFullYear()}`
    )
  }

  const parsed_content = (text) => {
    return mdParser.render(text)
  }

  return(
    <div style={{textAlign: 'left'}}>
      {publication ?
      <div>
        <Item >
        <Header>{publication.title}</Header>
        <Item.Image size="large" src={publication.thumbnail} />
            <Item.Content>
            <Item.Description>
            {/* {parsed_content(publication['content'])} */}
              {document.write(mdParser.render(publication.content))}
              </Item.Description>
                <Item.Extra><Icon name='time'/>{get_date()}  </Item.Extra>
                <Item.Extra>Writers: {publication.writer.map(x => {
                  return (
                    `${x}   `
                  )
                })} </Item.Extra>
            </Item.Content>
        </Item>
      </div>


      :

      <p></p>

      }


      <Header></Header>
      {error && <Message negative message={error}/>}
      </div>


  )
}
export default PubDetail;
