import React, {useEffect, useState} from 'react';
import CreatePubForm from '../components/CreatePubForm'
import {authenticationService, authAxios} from '../services'
import {api} from '../helpers/api'
import { Redirect } from 'react-router-dom';



const CreatePub = () => {
  const [isWriter, setIsWriter] = useState()
  const [sections, setSections] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  async function fetchData() {
    setLoading(true);
    try {
        const res = await authAxios.get(`${api.pub.base}createpub/`);
        setIsWriter(res.data.results[0].is_writer)
        setSections(res.data.results[0].available_sections)
        setLoading(false)
    } catch (err) {
        setError(err.message)
        setLoading(false)
    } 
  }

  useEffect(() => {
      if(authenticationService.isAuthenticated){
        fetchData()
      }
      console.log(isWriter)
    }, [])


  return(
    <>
    { loading == false && isWriter ? <CreatePubForm section={sections}/> : <Redirect to='/' />  }
    </>

  )

}
export default CreatePub;
