import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react';
import {Header,Item,Button} from 'semantic-ui-react'
import axios from 'axios'
import Message from '../components/Message'
import {useParams} from 'react-router-dom'
import {api} from '../helpers/api'
import {history} from '../helpers/history'



const PubDetail = () => {

  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)
  const params = useParams()


  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
          const res = await axios.delete(`${api.pub.list}${params['pubid']}/`);
          setPub(res.data);
          setLoading(false)
      } catch (err) {
          setError(err.message)
          setLoading(false)
      } 
    }
    fetchData();
  }, [])


  function handleDeleteButton() {
    axios.delete(`${api.pub.list}${params['pubid']}/`,{
        headers: {
            Authorization: 'Token 4f02ece2a0591ae26009f68bd0bce6b1ce60d09b',
            "Content-Type": "multipart/form-data"
        }}).then(res => {
            setLoading(true)
            history.push('/')
        }).catch(err => {
            console.log(err)
            setError(err)
        })
  }


  return(
    <div style={{textAlign: 'left'}}>
      {publication ?
      <div>
        <Item>
        <Header>{publication.title}</Header>
        <Item.Image size="small" src={publication.thumbnail} />
        </Item>
      </div>
      :
      <p></p>
      }
      <h2>Confirm you want to Delete the Post</h2>
        <Button onClick={handleDeleteButton} color='red'>Yes </Button>
      {error && <Message negative message={error}/>}
      </div>

  )
}
export default PubDetail;
