import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Message, Icon, Item, Divider} from 'semantic-ui-react'
import axios from 'axios'
import {authenticationService} from '../services'
import {parseIsoTimetoString} from '../helpers/timeConverter'
import { Redirect, NavLink } from 'react-router-dom'


const PubFeed = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            const res = await axios.get('http://127.0.0.1:8000/publication/');
            setPub(res.data.results);
            setLoading(false)
        } catch (err) {
            setError(err.message)
            setLoading(false)
        } 
      }
      fetchData();
    }, [])

  if (authenticationService.isAuthenticated == false){
    return <Redirect to='/' />
  }  
  return(

    <div>
      <Header as='h1'> Feed </Header>
      <Item.Group>
        {publication && (publication.map(pub => (
          <div key={pub.id}>
          <Item.Image size='small' src={pub.thumbnail} />
          <Item.Content>
          <NavLink to={pub.url}><Item.Header as='h1'>{pub.title}</Item.Header></NavLink>
            <Item.Extra><Icon name='time'/> : {parseIsoTimetoString(pub.date_time_posted)} | {pub.section}</Item.Extra>
          </Item.Content>
          <Divider />
          </div>


        )))}

      </Item.Group>

     </div>
  )
}
export default PubFeed;
