import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Message, Button, Icon} from 'semantic-ui-react'
import axios from 'axios'
import {authenticationService, authAxios} from '../services'
import {useParams} from 'react-router-dom'
import ListPub from '../components/ListPub'



const PubSection = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const params = useParams()

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            let ax = axios
            if (authenticationService.isAuthenticated){
                ax = authAxios
            }
            const res = await ax.get(`http://127.0.0.1:8000/${params['pubsection']}`);
            setPub(res.data.results);
            console.log(res.data.results)
            setLoading(false)
        } catch (err) {
            setError(err.message)
            setLoading(false)
        } 
      }
        fetchData()
    }, [params['pubsection']])

  return(
    <div>

      {publication && 
       (
        <>
        <Header as='h1'> {params['pubsection']}  
            {publication[0].is_subscribed ? 
               <Button animated='vertical'>
               <Button.Content hidden>Shop</Button.Content>
               <Button.Content visible>
                 <Icon name='shop' />
               </Button.Content>
                </Button> 
            :
                <Button positive>Subscrib </Button>
            }

        </Header>
        <ListPub publications={publication}/> 
        </>)
       }
      {error && <Message negative message={error}/>}



     </div>
  )
}
export default PubSection;
