import React from 'react'
import {
    Container,
    Menu,
    Input,
    Icon,
  } from 'semantic-ui-react';

import {NavLink} from 'react-router-dom';
import { authenticationService } from '../services'


const Navbar = () => {

    const handleLogout = () => {
        authenticationService.logout()
    }

    return (
    <div>
        <Menu fixed='top' inverted>
        <Container>
            <Menu.Item as='a' header>
            The Sun News
            </Menu.Item>
            {authenticationService.isAuthenticated ?
            <Menu.Item onClick={handleLogout} as='li'><Icon name='user outline'/> Logout </Menu.Item>
            :
            <NavLink to='/login/'><Menu.Item as='li'><Icon name='user outline'/> Login </Menu.Item></NavLink>
            }
            <NavLink to='/'> <Menu.Item as='li'>Home</Menu.Item> </NavLink>
            <NavLink to='/Sport/'> <Menu.Item as='li'>Sports</Menu.Item> </NavLink>
            <NavLink to='/World/'> <Menu.Item as='li'>World</Menu.Item> </NavLink>
            <NavLink to='/World/'> <Menu.Item as='li'>Business</Menu.Item> </NavLink>

            <Menu.Menu position='right'>
            <Menu.Item ><Input style={{height: '2.5em'}} placeholder='Search...'/></Menu.Item> 
            </Menu.Menu>
        </Container>
        </Menu>
    </div>
)}

export default Navbar;
