import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Item, Dimmer, Loader, Segment, Divider, Icon, Grid} from 'semantic-ui-react'
import axios from 'axios'
import Message from '../components/Message'
import { NavLink } from 'react-router-dom'


const PubHome = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            const res = await axios.get(`http://127.0.0.1:8000/publication/`);
            console.log(res.data.results);
            console.log(res);

            setPub(res.data.results);
            setLoading(false)
        } catch (err) {
            setError(err.message)
            setLoading(false)
        } 
      }
      fetchData();
    }, [])

    function timeConversion(millisec) {
      var seconds = (millisec / 1000).toFixed(0);
      var minutes = (millisec / (1000 * 60)).toFixed(0);
      var hours = (millisec / (1000 * 60 * 60)).toFixed(0);
      var days = (millisec / (1000 * 60 * 60 * 24)).toFixed(0);

      if (seconds < 60) {
          return seconds + " Sec";
      } else if (minutes < 60) {
          return minutes + " Min";
      } else if (hours < 24) {
          return hours + " Hrs";
      } else {
          return days + " Days"
      }
  }

    function parseISOString(s) {
      var b = s.split(/\D+/);
      console.log(b)
      var date_time = new Date(`${b[0]}, ${b[1]}, ${b[2]}, ${b[3]}, ${b[4]}, ${b[5]}, ${b[6]}`)
        date_time.map(x => {return console.log(x)})

      var today = new Date()
      console.log(date_time)
      return timeConversion(today.getTime() - date_time.getTime())
    }


    const filter_obj_section =(objlist, section) => {
        return objlist.filter(function(a) {
            return a.section === section 
        })

    }   


    const row_content = (pubList, section) => {
        if (publication) {
            return (
                filter_obj_section(pubList, section).slice(0, 3).map(x => {
                return(
                    <Grid.Column key={x.id} width={5}>
                        <Item >
                            <Item.Image size="small" src={x.thumbnail} />
                                <Item.Content>
                                <NavLink to={`/${x.section}/${x.id}`}><Item.Header as='h2'>{x.title}</Item.Header></NavLink>
                                    <Item.Extra><Icon name='time'/> : {parseISOString(x.date_time_posted)} | {x.section}</Item.Extra>
                                </Item.Content>
                        </Item>
                    </Grid.Column>
                )   
                }))

        } else {
            return (
                <Grid.Column>
                    <Segment>
                        <Dimmer active inverted>
                            <Loader inverted content='Loading...' />
                        </Dimmer>
                    </Segment>
                </Grid.Column>
            )

        }




    }




  return(

    <div>
        <Grid>

            <Grid.Row>
                <Grid.Column width={3}>
                    <Header as='h2'>News </Header>
                </Grid.Column>
                <Grid.Column width={13}>
                    <Divider />
                </Grid.Column>

                {publication ?

                    publication.slice(0,2).map(x => {
                        return(

                            <Grid.Column width={8} key={x.id}>
                            <Item>
                                <Item.Image size="large" src={x.thumbnail} />
                                    <Item.Content>
                                    <NavLink to={`/${x.section}/${x.id}`}><Item.Header as='h2'>{x.title}</Item.Header></NavLink>
                                        <Item.Extra><Icon name='time'/> : {parseISOString(x.date_time_posted)} | {x.section}</Item.Extra>
                                    </Item.Content>
                            </Item>
                        </Grid.Column>

                    )
                })

                :
                <p>No Highlights</p>
                }

            </Grid.Row>

            <Grid.Row>
                <Grid.Column width={3}>
                    <Header as='h2'>Sports </Header>
                </Grid.Column>
                <Grid.Column width={13}>
                    <Divider />
                </Grid.Column>
            {row_content(publication, 'Sports')}
            </Grid.Row>

            <Grid.Row>
                <Grid.Column width={3}>
                    <Header as='h2'>Business </Header>
                </Grid.Column>
                <Grid.Column width={13}>
                    <Divider />
                </Grid.Column>
                {row_content(publication, 'Business')}
            </Grid.Row>
            <Grid.Row>
                <Grid.Column width={3}>
                        <Header as='h2'>World </Header>
                    </Grid.Column>
                    <Grid.Column width={13}>
                        <Divider />
                    </Grid.Column>
                    {row_content(publication, 'World')}
            </Grid.Row>
        </Grid>




      <Item.Group>  
      {error && <Message negative message={error}/>}
      </Item.Group>




    </div>
  )
}
export default PubHome;
