import React, {useEffect, useState} from 'react';
import {Header, Image, Item, Icon} from 'semantic-ui-react'
import axios from 'axios'
import Message from '../components/Message'
import UpdatePubForm from '../components/UpdatePubForm';
import {api} from '../helpers/api'
import {useParams} from 'react-router-dom'



const UpdatePub = () => {
    const [publication, setPub] = useState()
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(null)
    const params = useParams()

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
          const res = await axios.get(`${api.pub.list}${params['pubid']}/`);
          setPub(res.data);
          setLoading(false)
      } catch (err) {
          setError(err.message)
          setLoading(false)
      } 
      console.log()
    }
    fetchData();
  }, [])






  return(
    <div>
      <UpdatePubForm/>

      <p>yey</p></div>

  )

}
export default UpdatePub;
