import React, {useState} from 'react'
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react'
import axios from 'axios'
import {api} from '../helpers/api'


const Login = () => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState('')

    function handleSubmit(e) {
        e.preventDefault()
        setLoading(true)
        axios.post(`${api.pub.loginUrl}`, {
                 "email": "admin@admin.com",
                 "password": "secret"
             } ,
            //  {
            // headers: {
            //     // Authorization: 'Token 4f02ece2a0591ae26009f68bd0bce6b1ce60d09b',
            //     // "Content-Type": "multipart/form-data"
            // }
        // }
        console.log(api.pub.loginUrl)
        ).then(res => {
                setLoading(true)

            }).catch(err => {
                console.log(err.message)
                setError(err.message)
            })
    }

    return (
  <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
    <Grid.Column style={{ maxWidth: 450 }}>
      <Header as='h2' color='blue' textAlign='center'>
       Log-in
      </Header>
      {error && <Message color='red'> {error} </Message> }
      <Form onSubmit={handleSubmit} size='large'>
        <Segment stacked>
          <Form.Input  onChange={e => setEmail(e.target.value)} fluid icon='user' iconPosition='left' placeholder='E-mail address' value={email} />
          <Form.Input
            onChange={e => setPassword(e.target.value)}
            fluid
            icon='lock'
            iconPosition='left'
            placeholder='Password'
            type='password'
            value={password}
          />

          <Button color='blue' fluid size='large'>
            Login
          </Button>
        </Segment>
      </Form>
      <Message>
        New to us? <a href='#'>Sign Up</a>
      </Message>
    </Grid.Column>
  </Grid>
    )
    }

export default Login
