import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Message, Icon, Item, Divider} from 'semantic-ui-react'
import axios from 'axios'
import {useParams, NavLink,} from 'react-router-dom'

import ListPub from '../components/ListPub'



const PubSection = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const params = useParams()

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            const res = await axios.get(`http://127.0.0.1:8000/${params['pubsection']}`);
            setPub(res.data.results);
            setLoading(false)
        } catch (err) {
            setError(err.message)
            setLoading(false)
        } 
      }
        fetchData()
    }, [params['pubsection']])

  return(
    <div>
      <Header as='h1'> {params['pubsection']} </Header>
      {publication && <ListPub publications={publication} /> }
      {error && <Message negative message={error}/>}
     </div>
  )
}
export default PubSection;
