import React, {useEffect, useState} from 'react'
import {
    Container,
    Menu,
    Input,
    Icon,
    Dropdown
  } from 'semantic-ui-react';
import {history} from '../helpers/history'
import {authAxios, authenticationService} from '../services'
import {NavLink} from 'react-router-dom';
import {api} from '../helpers/api'
import {ProfileMenu} from '../components/DropdownMenu'


const Navbar = () => {
    const [user, setUser] = useState('')
    const [error, setError] = useState(null)
    const loginOut = () => {
        authenticationService.logout()
        window.location.reload(false)
    }

    useEffect(() => {
      async function fetchData() {
        try {
            const res = await authAxios.get(api.auth.profile);
            setUser(res.data.results[0]);
        } catch (err) {
            setError(err.message)
        } 
      }
      if (authenticationService.isAuthenticated) {
        fetchData();
      }
    }, [])



    return (
    <div>
        <Menu fixed='top' inverted>
        <Container>
            <Menu.Item to='/' as='a' header>
            The Sun News
            </Menu.Item>
            {authenticationService.isAuthenticated ?
            <>
            <Menu.Menu>
                <Dropdown text=' Profile ' icon='user outline' pointing className='link item'>
                  <Dropdown.Menu>
                    <Dropdown.Item onClick={() => {history.push('/userprofile'); window.location.reload(false)}}>My Profile</Dropdown.Item>
                    <Dropdown.Item onClick={loginOut} as='a'>Log out</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Header>Options</Dropdown.Header>
                    {user.is_writer && ProfileMenu.writer}
                    {user.is_coordinator && ProfileMenu.coordinator}
                    {user.is_moderator && ProfileMenu.moderator}
                  </Dropdown.Menu>
                  </Dropdown>
                  </Menu.Menu>
            </>
            :
            <>
            <NavLink to='/login/'><Menu.Item as='li'><Icon name='user outline'/> Login </Menu.Item></NavLink>
            </>
            }
            <NavLink to='/'> <Menu.Item as='li'>Home</Menu.Item> </NavLink>
            <NavLink to='/Sport/'> <Menu.Item as='li'>Sports</Menu.Item> </NavLink>
            <NavLink to='/World/'> <Menu.Item as='li'>World</Menu.Item> </NavLink>
            <NavLink to='/World/'> <Menu.Item as='li'>Business</Menu.Item> </NavLink>

            <Menu.Menu position='right'>
            <Menu.Item ><Input style={{height: '2.5em'}} placeholder='Search...'/></Menu.Item> 
            </Menu.Menu>
        </Container>
        </Menu>
    </div>
)}

export default Navbar;
