import React, { Component, useRef, useState } from 'react'
import {
  Button,
  Checkbox,
  Form,
  Input,
  Radio,
  Select,
  TextArea,
  Header,
} from 'semantic-ui-react'
import axios from 'axios'
import {history} from '../helpers/history'

import {Message} from '../components/Message'

const CreatePubForm = () => {
    const [title, setTitle] = useState('')
    const [section, setSection] = useState('')
    const [content, setContent] = useState('')
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(null)
    const [thumbnail, setThumbnail] = useState('')
    const fileInputRef = useRef()

    function handleFormSubmit(e) {
        e.preventDefault();

        const formData = new FormData()
        formData.append('thumbnail', thumbnail)
        formData.append('title', title)
        formData.append('content', content)
        formData.append('section', section)

        console.log(formData)

        axios.post('http://127.0.0.1:8000/publication/', formData, {
            headers: {
                Authorization: 'Token 4f02ece2a0591ae26009f68bd0bce6b1ce60d09b',
                "Content-Type": "multipart/form-data"
            }}).then(res => {
                setLoading(false)
                history.push('/')
                console.log(res)
            }).catch(err => {
                console.log(err)
                setError(err)
            })


    }
    return(
        <div>
        <Header as='h2'>Create your publication</Header>
            <div style={{textAlign: 'left'}}>
                <Form onSubmit={handleFormSubmit}>
                <Form.Field value={section} onChange={e=> setSection(e.target.value)} label='Section' control='select'>
                    <option value=''>---</option>
                    <option value='Business'>Business</option>
                    <option value='Sports'>Sports</option>
                    <option value='World'>World</option>
                </Form.Field>
                <Form.Field>
                    <label>Title</label>
                    <input value={title} onChange={e => setTitle(e.target.value)} placeholder='Title' />
                </Form.Field>
                    <Form.TextArea value={content} onChange={e => setContent(e.target.value)} label='Content' placeholder='Publication content ...' />
                <Form.Field hidden>
                    <label>Writer</label>
                    <input placeholder='Writer' />
                </Form.Field>

                <Form.Field>
                    {thumbnail?<input disabled value={thumbnail.name}/>:''}
                    <Button onClick={() => fileInputRef.current.click()} content='Upload a thumbnail' labelPosition='left' icon='image'/>
                    <input onChange={e => setThumbnail(e.target.files[0])}ref={fileInputRef} type='file' hidden />
                </Form.Field>
                <Form.Field>
                    <Checkbox label='I agree to the Terms and Conditions' />
                </Form.Field>
                <Button primary fluid type='submit'>Submit</Button>

                </Form>
        </div>
    </div>
    )



}



export default CreatePubForm
