import React, { useRef, useState } from 'react'
import {
  Button,
  Checkbox,
  Form,
  Header,
} from 'semantic-ui-react'
import {authAxios} from '../services'
import {history} from '../helpers/history'
import {api} from '../helpers/api'
import Message from '../components/Message'
import MarkdownIt from 'markdown-it'
import MdEditor from 'react-markdown-editor-lite'
import 'react-markdown-editor-lite/lib/index.css';


const mdParser = new MarkdownIt(/* Markdown-it options */);

const CreateColumnForm = () => {
    const [title, setTitle] = useState('')
    const [section, setSection] = useState('')
    const [content, setContent] = useState('')
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(null)
    const [thumbnail, setThumbnail] = useState('')
    const fileInputRef = useRef()

    function handleFormSubmit(e) {
        e.preventDefault();
        const formData = new FormData()
        formData.append('thumbnail', thumbnail)
        formData.append('title', title)
        formData.append('content', content)
        formData.append('section', section)
        authAxios.post(api.pub.list, formData, {
            headers: {
                "Content-Type": "multipart/form-data"
            }}).then(res => {
                setLoading(true)
                history.push('/')
                window.location.reload(false)
                console.log(res)
            }).catch(err => {
                console.log(err)
                setError(err)
            })
    }

    return(
        <div>
            <Header as='h2'>Create Publication Form</Header>
            {error && <Message danger message={error.message} />}
            <div style={{textAlign: 'left'}}>
                <Form onSubmit={handleFormSubmit}>
                <Form.Field>
                    <label>Column Title</label>
                    <input value={title} onChange={e => setTitle(e.target.value)} placeholder='Title' />
                </Form.Field>
                <Button primary loading={loading} disabled={loading} fluid type='submit'>Submit</Button>

                </Form>
        </div>


    </div>
    )



}



export default CreateColumnForm
