import React, { Component, useRef, useState, useEffect } from 'react'
import {
  Button,
  Checkbox,
  Form,
  Header,
} from 'semantic-ui-react'
import axios from 'axios'
import {history} from '../helpers/history'
import {api} from '../helpers/api'
import Message from '../components/Message'
import MarkdownIt from 'markdown-it'
import MdEditor from 'react-markdown-editor-lite'
import {useParams} from 'react-router-dom'
import 'react-markdown-editor-lite/lib/index.css';

const params = useParams()
const mdParser = new MarkdownIt(/* Markdown-it options */);

const UpdatePubForm = () => {
    const [title, setTitle] = useState('')
    const [section, setSection] = useState('')
    const [content, setContent] = useState('')
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(null)
    const [publication, setPub] = useState()
    const [thumbnail, setThumbnail] = useState('')
    const fileInputRef = useRef()


    useEffect(() => {
        async function fetchData() {
          setLoading(true);
          try {
              const res = await axios.get(`${api.pub.list}${params['pubid']}/`);
              setPub(res.data);
              setLoading(false)
          } catch (err) {
              setError(err.message)
              setLoading(false)
          } 
          console.log()
        }
        fetchData();
      }, [])

      const get_date = () => {
        let d = new Date(publication.date_time_posted)
        let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
        return (
          `${d.getDate()} ${months[d.getMonth()]} ${d.getFullYear()}`
        )
      }


    function handleFormSubmit(e) {
        e.preventDefault();
        const formData = new FormData()
        formData.append('thumbnail', thumbnail)
        formData.append('title', title)
        formData.append('content', content)
        formData.append('section', section)
        axios.post(api.pub.list, formData, {
            headers: {
                Authorization: 'Token 4f02ece2a0591ae26009f68bd0bce6b1ce60d09b',
                "Content-Type": "multipart/form-data"
            }}).then(res => {
                setLoading(true)
                history.push('/')
                console.log(res)
            }).catch(err => {
                console.log(err)
                setError(err)
            })
    }

    function handleEditorChange({html, text}) { 
        setContent(text)  
        console.log('handleEditorChange', html, text)
      }


    return(
        <div>
            <Header as='h2'>Update Publication Form</Header>
            {error && <Message danger message={error.message} />}
            <div style={{textAlign: 'left'}}>
                <Form onSubmit={handleFormSubmit}>
                <Form.Field value={section} onChange={e=> setSection(e.target.value)} label='Section' control='select'>
                    <option value=''>---</option>
                    <option value='Business'>Business</option>
                    <option value='Sports'>Sports</option>
                    <option value='World'>World</option>
                </Form.Field>
                <Form.Field>
                    <label>Title</label>
                    <input value={title} onChange={e => setTitle(e.target.value)} placeholder='Title' />
                </Form.Field>
                <Form.Field>
                <label>Content</label>
                <MdEditor
                    style={{ height: "500px" }}
                    renderHTML={(text) => mdParser.render(text)}
                    onChange={handleEditorChange}
                        />
                </Form.Field>
                <Form.Field>
                    {thumbnail?<input disabled value={thumbnail.name}/>:''}
                    <Button type='button'onClick={() => fileInputRef.current.click()} content='Upload a thumbnail' labelPosition='left' icon='image'/>
                    <input onChange={e => setThumbnail(e.target.files[0])}ref={fileInputRef} type='file' hidden />
                </Form.Field>
                <Form.Field>
                    <Checkbox label='I agree to the Terms and Conditions' />
                </Form.Field>
                <Button primary loading={loading} disabled={loading} fluid type='submit'>Submit</Button>

                </Form>
        </div>


    </div>
    )



}



export default UpdatePubForm
