import React from 'react'
import { Divider, Transition, Loader } from 'semantic-ui-react'

const Loading = ({loading, children}) => {
    return (
      <div>

        <Transition visible={loading? false: true} animation='scale' duration={1000}>
            {children}
        </Transition>

        <Transition visible={loading} animation='scale' duration={500}>
            <Loader active inline='centered' />
        </Transition>


      </div>
    )
  }

  export default Loading
