import React, {useEffect, useState} from 'react';
import axios from 'axios'
import Message from '../components/Message'
import UpdatePubForm from '../components/UpdatePubForm';
import {api} from '../helpers/api'
import {useParams, Redirect} from 'react-router-dom'



const UpdatePub = () => {
    const [publication, setPub] = useState()
    const [loading, setLoading] = useState(true)
    const [error, setError] = useState(null)
    const params = useParams()

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
          const res = await axios.get(`${api.pub.list}${params['pubid']}/`);
          setPub(res.data);
          setLoading(false)
      } catch (err) {
          setError(err.message)
          setLoading(false)
      } 
      console.log()
    }
    fetchData();
  }, [])
  if (publication){
    if (publication.is_author == false){
      return <Redirect to='/'/>
    }
  }


  return(
    <div>
      {error && <Message danger message={error.message} />}

      {publication &&
      <UpdatePubForm data={publication}/>
      }


      </div>

  )

}
export default UpdatePub;
