import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Item, Dimmer, Loader, Segment, Divider, Icon, Grid} from 'semantic-ui-react'
import axios from 'axios'
import Message from '../components/Message'


const PubHome = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            // const res = await axios.get('http://127.0.0.1:8000/feed/', {headers: {Authorization: 'Token 4f02ece2a0591ae26009f68bd0bce6b1ce60d09b' }});
            const res = await axios.get('http://127.0.0.1:8000/publication/');
            console.log(res.data.results);

            setPub(res.data.results);
            console.log(publication)
            setLoading(false)
        } catch (err) {
            setError(err.message)
            setLoading(false)
        } 
      }
      fetchData();
    }, [])

    function timeConversion(millisec) {
      var seconds = (millisec / 1000).toFixed(0);
      var minutes = (millisec / (1000 * 60)).toFixed(0);
      var hours = (millisec / (1000 * 60 * 60)).toFixed(0);
      var days = (millisec / (1000 * 60 * 60 * 24)).toFixed(0);

      if (seconds < 60) {
          return seconds + " Sec";
      } else if (minutes < 60) {
          return minutes + " Min";
      } else if (hours < 24) {
          return hours + " Hrs";
      } else {
          return days + " Days"
      }
  }

    function parseISOString(s) {
      var b = s.split(/\D+/);
      var date_time = new Date(Date.UTC(b[0], --b[1], b[2], b[3], b[4], b[5], b[6]))
      var today = new Date()
      return timeConversion(today.getTime() - date_time.getTime())
    }

    const get_first_pub_of_section = (pub, section) => {
        var publist = []
        var filtered_pub = pub.filter(function(a) {
            if (a.section == section)
            return a
        })

        console.log(filtered_pub)

        if (filtered_pub > 5 ) {
            for (let i = 0 ; i <= 5; i++) {
                publist.push(filtered_pub[i])

            }
            return publist
        } else {
            return filtered_pub
        }
    }





  return(

    <div>
            <Header as='h1'> The Sun News </Header>



        <Grid>
        <Header as='h2'> | News </Header>
            <Grid.Row>


                { publication ?
      get_first_pub_of_section(publication, 'Sports').map(x => {
        return(
            <Grid.Column width={5}>
                <Item key={x.id}>
                    <Item.Image size="small" src={x.thumbnail} />
                        <Item.Content>
                            <Item.Header as='h2'>{x.title}</Item.Header>
                            <Item.Extra><Icon name='time'/> : {parseISOString(x.date_time_posted)} | {x.section}</Item.Extra>
                        </Item.Content>
                </Item>
          </Grid.Column>
        )
      })
      :
      <Segment>
      <Dimmer active inverted>
        <Loader inverted content='Loading' />
      </Dimmer>
      </Segment>
      }


            </Grid.Row>
        </Grid>

      <Divider/>


      <Item.Group>  
      {error && <Message negative message={error}/>}
      </Item.Group>




    </div>
  )
}
export default PubHome;
