import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Item, Image} from 'semantic-ui-react'
import axios from 'axios'


const PubFeed = () => {
  const [publication, setPub] = useState(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            const res = await axios.get('http://127.0.0.1:8000/feed/', {headers: {Authorization: 'Token 4f02ece2a0591ae26009f68bd0bce6b1ce60d09b' }});
            console.log(publication);

            setPub(res.data)
            setLoading(false)
        } catch (error) {
            setError(error)
            setLoading(false)
        } 
      }
      fetchData();
    }, [])


  return(
    <div>
      <Header>Pub Feed </Header>

      <Item.Group>
        {console.log(publication)}

      </Item.Group>




    </div>
  )
}
export default PubFeed;
