import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react';
import {Header,Item, Icon, Button, Modal} from 'semantic-ui-react'
import axios from 'axios'
import Message from '../components/Message'
import {useParams, NavLink} from 'react-router-dom'
import {api} from '../helpers/api'
import ReactMarkdown from 'react-markdown'









const PubDetail = () => {

  const [open, setOpen] = React.useState(false)
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)
  const params = useParams()


  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
          const res = await axios.get(`${api.pub.list}${params['pubid']}/`);
          setPub(res.data);
          setLoading(false)
      } catch (err) {
          setError(err.message)
          setLoading(false)
      } 
    }
    fetchData();
  }, [])

  const get_date = () => {
    let d = new Date(publication.date_time_posted)
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    return (
      `${d.getDate()} ${months[d.getMonth()]} ${d.getFullYear()}`
    )
  }


  return(
    <div style={{textAlign: 'left'}}>
      {publication ?
      <div>
        <Item >
        <Header>{publication.title}</Header>
        <Item.Image size="large" src={publication.thumbnail} />
            <Item.Content>
            <Item.Description>
            <ReactMarkdown source={publication.content}/>
              </Item.Description>
                <Item.Extra><Icon name='time'/>{get_date()}  </Item.Extra>
                <Item.Extra>Writer(s): {publication.writer.map(x => {
                  return (
                    `${x}   `
                  )
                })} </Item.Extra>
            </Item.Content>
        </Item>
      </div>


      :

      <p></p>

      }

<NavLink to={`/${params['pubsection']}/${params['pubid']}/update`}><Button>Update </Button></NavLink>
{/* <NavLink to={`/${params['pubsection']}/${params['pubid']}/delete`}><Button color='red'>Delete </Button></NavLink> */}
<Modal
      basic
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      size='small'
      trigger={<Button color='red'>Delete </Button>}
    >
      <Header icon>
        <Icon name='archive' />
        Archive Old Messages
      </Header>
      <Modal.Content>
        <p>
          Your inbox is getting full, would you like us to enable automatic
          archiving of old messages?
        </p>
      </Modal.Content>
      <Modal.Actions>
        <Button basic color='red' inverted onClick={() => setOpen(false)}>
          <Icon name='remove' /> No
        </Button>
        <Button color='green' inverted onClick={() => setOpen(false)}>
          <Icon name='checkmark' /> Yes
        </Button>
      </Modal.Actions>
    </Modal>

      {error && <Message negative message={error}/>}


      </div>

  )
}
export default PubDetail;
