import React from 'react'
import { NavLink } from 'react-router-dom'
import {Dropdown} from 'semantic-ui-react'
import {history} from '../helpers/history'


const DropdownWriter = () => {
    return (
    <>
    <Dropdown.Item onClick={() => {history.push('/createpublication'); window.location.reload(false)}} as='a'>Create a Publication</Dropdown.Item>
    <Dropdown.Item onClick={() => {history.push('/createpublication'); window.location.reload(false)}} as='a'>My Publication</Dropdown.Item>
    </>)
}

const DropdownModerator = () => {
    return (
    <>
    <Dropdown.Item onClick={() => {history.push('/pendingPublication/'); window.location.reload(false)}} as='a'>Review Publication</Dropdown.Item>
    </>
    )

}

const DropdownCoordinator = () => {
    return (
    <>
    <Dropdown.Item onClick={() => {history.push('/createColumn/'); window.location.reload(false)}} as='a'>Create a Column</Dropdown.Item>
    <Dropdown.Item onClick={() => {history.push('/myColumn/'); window.location.reload(false)}} as='a'>My Columns</Dropdown.Item>
    </>
    )
}


export const ProfileMenu = {
    writer: <DropdownWriter />,
    coordinator: <DropdownCoordinator />,
    moderator: <DropdownModerator />,
}
