import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Message, Button, Icon, Grid} from 'semantic-ui-react'
import axios from 'axios'
import {authenticationService, authAxios} from '../services'
import {useParams} from 'react-router-dom'
import ListPub from '../components/ListPub'
import {api} from '../helpers/api'
import {history} from '../helpers/history'


const PubSection = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [isSubscribed, setIsSubscribed] = useState(false)
  const [buttonActive, setButtonActive] = useState(false)
  const params = useParams()

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            let ax = axios
            if (authenticationService.isAuthenticated){
                ax = authAxios
            }
            const res = await ax.get(`http://127.0.0.1:8000/${params['pubsection']}`);
            setPub(res.data.results);
            setIsSubscribed(res.data.results[0].is_subscribed)
            setLoading(false)
        } catch (err) {
            setError(err.message)
            setLoading(false)
        } 
      }
        fetchData()
    }, [params['pubsection']])

    function handleSubscription() {
        if (authenticationService.isAuthenticated) {
            if (publication[0].is_subscribed) {
                authAxios.delete(`${api.pub.base}subscription/${isSubscribed}`).then(res => {

                }).catch(err => {
                    console.log(err)
                    setError(err.message)
                })    
            } else {
                authAxios.post(`${api.pub.base}createSubscription/`, 
                {
                    "column": params['pubsection'],
                }
                ).then(res => {


                }).catch(err => {
                    console.log(err)
                    setError(err.message)
                })
            }
        } else {
            history.push('/')
            window.location.reload(false)
        }
    }
  return(
    <div>
        <Header as='h1'> {params['pubsection']}  {publication && (
                    <>
                    {
                        isSubscribed ? 
                        <Button disabled={buttonActive} onClick={handleSubscription} animated>
                        <Button.Content hidden>Unsubscribe</Button.Content>
                        <Button.Content visible>
                          <Icon name='close' />
                        </Button.Content>
                         </Button> 
                     :
                         <Button disabled={buttonActive} onClick={handleSubscription} animated>
                             <Button.Content hidden>Subscribe</Button.Content>
                             <Button.Content visible>
                             <Icon name='signup' />
                             </Button.Content>
                         </Button>
                    }
                    </>
                )}</Header>  

      {publication &&
        <ListPub publications={publication}/> 
       }

      {error && <Message negative message={error.message}/>}



     </div>
  )
}
export default PubSection;
