import React, { useState, useEffect } from 'react'
import {authAxios, authenticationService} from '../services'
import { Card, Icon} from 'semantic-ui-react'
import {api} from '../helpers/api'
import { Redirect } from 'react-router-dom'


const UserProfile = () => {
    const [error, setError] = useState(null)
    const [user, setUser] = useState('')


useEffect(() => {
    async function fetchData() {
      try {
          const res = await authAxios.get(api.auth.profile);
          setUser(res.data.results[0]);
      } catch (err) {
          setError(err.message)
      } 
    }
    fetchData();
  }, [])

  if (authenticationService.isAuthenticated === false) {
      return <Redirect to='/'/>
  }

  return (
    <div style={{textAlign: 'left'}}>
    <Card>
        {user && 
            <>
            <Card.Content>
            <Card.Header>{user.email}</Card.Header>
            <Card.Meta>
                <span className='date'> Status:  
                    {user.is_writer && (
                        <> Writer <Icon name='check' /> </>
                    )}
                    {user.is_moderator && (
                        <>Moderator <Icon name='check' /> </>
                    )}

                    {user.is_coordinator && (
                        <>Coordinator <Icon name='check' /> </>
                    )}

                </span>
            </Card.Meta>
            </Card.Content>
            <Card.Content extra>
            <a>
                <Icon name='user' />
                Active
            </a>
            </Card.Content>
            </>
        }
  </Card>
  </div>
  )
}

export default UserProfile
