import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Message, Icon, Item} from 'semantic-ui-react'
import axios from 'axios'
import {authenticationService} from '../services'
import {parseIsoTimetoString} from '../helpers/timeConverter'


const PubFeed = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            const res = await axios.get('http://127.0.0.1:8000/publication/');
            console.log(res.data.results);
            setPub(res.data.results);
            setLoading(false)
        } catch (err) {
            setError(err.message)
            setLoading(false)
        } 
      }
      fetchData();
    }, [])

    const items = []

    if (publication){
      publication.map(pub => {
        items.push(
          { childKey: pub.id,
            image: pub.thumbnail,
            header: pub.title,
            description: pub.content.substr(1, 100),
            meta: 'Metadata',
            extra: <><Icon name='time'/> : {parseIsoTimetoString(pub.date_time_posted)} | {pub.section}</>,}

        )
      })
    }










  return(

    <div>
      <Header as='h1'> Feed </Header>
      {publication && <Item.Group items={items}/>}

     </div>
  )
}
export default PubFeed;
