import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Item, Dimmer, Loader, Segment, Divider} from 'semantic-ui-react'
import axios from 'axios'
import Message from '../components/Message'


const PubFeed = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            // const res = await axios.get('http://127.0.0.1:8000/feed/', {headers: {Authorization: 'Token 4f02ece2a0591ae26009f68bd0bce6b1ce60d09b' }});
            const res = await axios.get('http://127.0.0.1:8000/publication/');
            console.log(res.data.results);

            setPub(res.data.results);
            console.log(publication)
            setLoading(false)
        } catch (err) {
            setError(err.message)
            setLoading(false)
        } 
      }
      fetchData();
    }, [])

    function parseISOString(s) {
      var b = s.split(/\D+/);
      var date_time = new Date(Date.UTC(b[0], --b[1], b[2], b[3], b[4], b[5], b[6]))
      var today = new Date()
      return date_time.getTime() - today.getTime()
    }

  return(

    <div>
      <Header> Feed </Header>
      {publication ?
      publication.map(x => {
        return(
          <div>
            {console.log(parseISOString(x.date_time_posted))}
          <Item key={x.id}>
          <Item.Image size="medium" src={x.thumbnail} />
            <Item.Content>
              <Item.Header as='a'>{x.title}</Item.Header>
              <Item.Description>
              {x.content}
              </Item.Description>
              <Item.Extra>Posted on : {parseISOString(x.date_time_posted)}</Item.Extra>
            </Item.Content>
          </Item>
          <Divider/>
          </div>


        )
      })
      :
      <Segment>
      <Dimmer active inverted>
        <Loader inverted content='Loading' />
      </Dimmer>
      </Segment>
      }

      <Item.Group>

      {error && <Message negative message={error}/>}
      </Item.Group>




    </div>
  )
}
export default PubFeed;
