import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Item, Image, Dimmer, Loader} from 'semantic-ui-react'
import axios from 'axios'


const PubFeed = () => {
  const [publication, setPub] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  useEffect(() => {
      async function fetchData() {
        setLoading(true);
        try {
            // const res = await axios.get('http://127.0.0.1:8000/feed/', {headers: {Authorization: 'Token 4f02ece2a0591ae26009f68bd0bce6b1ce60d09b' }});
            const res = await axios.get('http://127.0.0.1:8000/publication/');
            console.log(res.data.results);

            setPub(res.data.results);
            console.log(publication)
            setLoading(false)
        } catch (error) {
            setError(error)
            setLoading(false)
        } 
      }
      fetchData();
    }, [])


  return(

    <div>
      <Header> Feed </Header>
      {publication ?
      publication.map(x => {
        return(
          <Item key={x.id}>
            <Item.Content>
              <Item.Header as='a'>{x.title}</Item.Header>
        <Item.Meta>{x.content}</Item.Meta>
              <Item.Description>
                <Image src={x.thumbnail} />
              </Item.Description>
        <Item.Extra>Posted : {x.date_time_posted}</Item.Extra>
            </Item.Content>
          </Item>
        )
      })
      :
      <Dimmer active inverted>
        <Loader inverted content='Loading' />
      </Dimmer>
      }
      <Item.Group>


      </Item.Group>




    </div>
  )
}
export default PubFeed;
