import 'antd/dist/antd.css'
import React, {useEffect, useState} from 'react'
import {Header, Item, Dimmer, Loader, Segment, Divider, Icon, Grid} from 'semantic-ui-react'
import axios from 'axios'
import Message from '../components/Message'
import { NavLink } from 'react-router-dom'
import {api} from '../helpers/api'
import {parseIsoTimetoString} from '../helpers/timeConverter'




const PubHome = () => {
  const [publication, setPub] = useState()
  const [error, setError] = useState(null)

  useEffect(() => {
      async function fetchData() {
        try {
            const res = await axios.get(api.pub.list);
            setPub(res.data.results);
        } catch (err) {
            setError(err.message)
        } 
      }
      fetchData();
    }, [])

    const filter_obj_section =(objlist, section) => {
        return objlist.filter(function(a) {
            return a.section === section 
        })

    }   

    const row_content = (pubList, section) => {
        if (publication) {
            return (
                filter_obj_section(pubList, section).slice(0, 3).map(x => {
                return(
                    <Grid.Column key={x.id} width={5}>
                        <Item >
                            <Item.Image size="small" src={x.thumbnail} />
                                <Item.Content>
                                <NavLink to={`/${x.section}/${x.id}`}><Item.Header as='h2'>{x.title}</Item.Header></NavLink>
                                    <Item.Extra><Icon name='time'/> : {parseIsoTimetoString(x.date_time_posted)} | {x.section}</Item.Extra>
                                </Item.Content>
                        </Item>
                    </Grid.Column>
                )   
                }))
        } else {
            return (
                <Grid.Column>
                    <Segment>
                        <Dimmer active inverted>
                            <Loader inverted content='Loading...' />
                        </Dimmer>
                    </Segment>
                </Grid.Column>
            )  
        }                   
    }


  return(
    <div>
        <Grid>
            <Grid.Row>
                <Grid.Column width={3}>
                    <Header as='h2'>News </Header>
                </Grid.Column>
                <Grid.Column width={13}>
                    <Divider />
                </Grid.Column>
                {
                publication ?
                    publication.slice(0,2).map(x => {
                        return(
                            <Grid.Column width={8} key={x.id}>
                            <Item>
                                <Item.Image size="large" src={x.thumbnail} />
                                    <Item.Content>
                                    <NavLink to={`/${x.section}/${x.id}`}><Item.Header as='h2'>{x.title}</Item.Header></NavLink>
                                        <Item.Extra><Icon name='time'/> : {parseISOString(x.date_time_posted)} | {x.section}</Item.Extra>
                                    </Item.Content>
                            </Item>
                        </Grid.Column>
                        )
                    })
                :
                <p>No Highlights</p>
                }
            </Grid.Row>

            <Grid.Row>
                <Grid.Column width={3}>
                    <Header as='h2'>Sports </Header>
                </Grid.Column>
                <Grid.Column width={13}>
                    <Divider />
                </Grid.Column>
            {row_content(publication, 'Sports')}
            </Grid.Row>

            <Grid.Row>
                <Grid.Column width={3}>
                    <Header as='h2'>Business </Header>
                </Grid.Column>
                <Grid.Column width={13}>
                    <Divider />
                </Grid.Column>
                {row_content(publication, 'Business')}
            </Grid.Row>

            <Grid.Row>
                <Grid.Column width={3}>
                        <Header as='h2'>World </Header>
                    </Grid.Column>
                    <Grid.Column width={13}>
                        <Divider />
                    </Grid.Column>
                    {row_content(publication, 'World')}
            </Grid.Row>

        </Grid>
      <Item.Group>  
      {error && <Message negative message={error}/>}
      </Item.Group>
    </div>
  )
}
export default PubHome;
