import React, {useState} from 'react'
import { Transition, Loader, Dimmer, Segment, } from 'semantic-ui-react'

const Loading = ({loading, children}) => {
    const [pub, setpub] = useState(false)

    if (children) {
        setTimeout(() => setpub(true), 5000)
    }

    return (
      <div>

        <Segment>
            <Dimmer active={loading} inverted>
                    <Loader size='medium' />
            </Dimmer>
            {children}
        </Segment>







      </div>
    )
  }

  export default Loading
