import React, {useState, useEffect}from 'react'
import {
    Container,
    Dropdown,
    Image,
    Menu,
    Search,
    Icon,
  } from 'semantic-ui-react';

import {NavLink} from 'react-router-dom';

const Navbar = () => {

    const [searchVal, setSearchVal] = useState('');

    const handleSearchChange = (e, { value }) => {
        e.preventDefault();
        setSearchVal(value) 
    }


    return (
    <div>
        <Menu fixed='top' inverted>
        <Container>
            <Menu.Item as='a' header>
            The Sun News
            </Menu.Item>
            <NavLink to='/'> <Menu.Item as='li'>Home</Menu.Item> </NavLink>
            <NavLink to='/Sport/'> <Menu.Item as='li'>Sport</Menu.Item> </NavLink>
            <NavLink to='/World/'> <Menu.Item as='li'>World</Menu.Item> </NavLink>
            <NavLink to='/World/'> <Menu.Item as='li'>Business</Menu.Item> </NavLink>

            <Menu.Menu position='right'>
            <Menu.Item as='li'> Login </Menu.Item>

            <Dropdown search text={<Icon name='search'/>} className='icon'>
                  <Dropdown.Menu>
                    <Dropdown.Item><Search
                aligned='right'
                loading={false}
                onResultSelect='none'
                onSearchChange={handleSearchChange}
                results='/'
                value={searchVal}
            /></Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>



            </Menu.Menu>

        </Container>
        </Menu>
    </div>
)}

export default Navbar;
